import Image from "next/image"

interface SEOOptimizedImageProps {
  src: string
  alt: string
  width: number
  height: number
  className?: string
  priority?: boolean
  sizes?: string
}

export function SEOOptimizedImage({
  src,
  alt,
  width,
  height,
  className,
  priority = false,
  sizes,
}: SEOOptimizedImageProps) {
  return (
    <Image
      src={src || "/placeholder.svg"}
      alt={alt}
      width={width}
      height={height}
      className={className}
      priority={priority}
      sizes={sizes}
      loading={priority ? "eager" : "lazy"}
      quality={85}
      placeholder="blur"
      blurDataURL="data:image/jpeg;base64,"
    />
  )
}
